mapper.select:
SELECT nt.id, nt.code, nt.name FROM notify_template nt INNER JOIN notify_send_setting nss on nss.code = nt.business_type
AND nss.code = #{type}
WHERE nss.fd_level = #{level} AND nt.message_type = #{messageType}

SELECT nt.id, nt.code, nt.name, nss.name AS type, nt.is_predefined AS isPredefined FROM notify_template nt LEFT JOIN notify_send_setting nss on nss.code = nt.business_type WHERE nt.message_type = 'pm' AND nss.fd_level = #{level}
AND nss.name LIKE concat('%',#{type},'%')
AND nt.code LIKE concat('%',#{code},'%')
AND nt.name LIKE concat('%',#{name},'%')
AND nt.is_predefined = #{isPredefined}
AND( nt.code LIKE concat('%',#{params},'%') OR nt.name LIKE concat('%',#{params},'%') OR nss.name LIKE concat('%',#{params},'%') )

SELECT nt.id, nt.code, nt.name, nss.name AS type, nt.is_predefined AS isPredefined FROM notify_template nt LEFT JOIN notify_send_setting nss on nss.code = nt.business_type WHERE nt.message_type = 'email' AND nss.fd_level = #{level}
AND nss.name LIKE concat('%',#{type},'%')
AND nt.code LIKE concat('%',#{code},'%')
AND nt.name LIKE concat('%',#{name},'%')
AND nt.is_predefined = #{isPredefined}
AND( nt.code LIKE concat('%',#{params},'%') OR nt.name LIKE concat('%',#{params},'%') OR nss.name LIKE concat('%',#{params},'%') )

SELECT nt.id, nt.code, nt.name FROM notify_template nt INNER JOIN notify_send_setting nss on nss.code = nt.business_type
AND nss.code = #{type}
WHERE nss.fd_level = #{level}
